import React, { useState } from 'react';
import { storage } from 'firebase/storage';

import { useDropzone } from 'react-dropzone';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import Button from '@mui/material/Button';

const FileUploader = () => {
  const [files, setFiles] = useState([]);

  const onDrop = (acceptedFiles) => {
    setFiles(acceptedFiles);
  };

  const handleFileUpload = () => {
    files.forEach((file) => {
      const uploadTask = storage.ref(`uploads/${file.name}`).put(file);

      uploadTask.on(
        'state_changed',
        (snapshot) => {
          // Puedes mostrar el progreso de carga aquí si deseas
        },
        (error) => {
          // Maneja los errores de carga aquí
          console.error(error);
        },
        () => {
          // La carga se ha completado exitosamente
          console.log('Archivo subido con éxito');
        }
      );
    });
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({ onDrop });

  return (
    <div>
      <div
        {...getRootProps()}
        style={{
          border: '2px dashed #000',
          padding: '20px',
          textAlign: 'center',
          cursor: 'pointer',
          backgroundColor: isDragActive ? '#efefef' : '#fff',
        }}
      >
        <input {...getInputProps()} />
        {isDragActive ? (
          <p>Suelta los archivos aquí...</p>
        ) : (
          <p>Arrastra y suelta archivos aquí, o haz clic para seleccionar archivos</p>
        )}
      </div>
      {files.length > 0 && (
        <div>
          <h4>Archivos seleccionados:</h4>
          <ul>
            {files.map((file) => (
              <li key={file.name}>{file.name}</li>
            ))}
          </ul>
          <Button variant="contained" startIcon={<CloudUploadIcon />} onClick={handleFileUpload}>
            Subir archivos
          </Button>
        </div>
      )}
    </div>
  );
};

export default FileUploader;
