import { useState, useEffect } from "react";
import { Grid, Paper, Typography } from "@mui/material";
import {readDataField, readData } from '../utils/firebaseService';

const CargasAdmin = () => {
  const [cargas, setCargas] = useState([]);
  const [uid, setUid] = useState("");

  useEffect(() => {
    readDataField('projects/proj_meqjHnqVDFjzhizHdj6Fjq/data/Loads', "userConsig", )
      .then((data) => {
        const cargasArray = Object.values(data);
        setCargas(cargasArray);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  const cargaStatuses = ["Publicada", "En recoleccion", "En tránsito", "En entrega", "Finalizada"];

  const renderCargasByStatus = (status) => {
    const cargasByStatus = cargas.filter((carga) => carga.config.config.estatusCarga === status);

    return cargasByStatus.map((carga) => (
      <Grid item xs={12} sm={6} md={4} lg={3} key={carga.IdLoad}>
        <Paper>
          <Typography>{carga.IdLoad}</Typography>
          {/* Otros detalles de la carga */}
        </Paper>
      </Grid>
    ));
  };

  return (
    <Grid container spacing={2}>
      {cargaStatuses.map((status) => (
        <Grid item xs={12} key={status}>
          <Typography variant="h6" gutterBottom>
            {status}
          </Typography>
          <Grid container spacing={2}>
            {renderCargasByStatus(status)}
          </Grid>
        </Grid>
      ))}
    </Grid>
  );
};

export default CargasAdmin;
