import React from "react";
import { Button } from "@mui/material";
import { storage } from "../utils/firebase";

const DescargarMachote = () => {
  const handleDescargarMachote = () => {
    const storageRef = storage.ref();
    const machoteRef = storageRef.child("nombre-del-machote.xlsx"); // Reemplaza "nombre-del-machote.xlsx" con el nombre del archivo machote

    machoteRef
      .getDownloadURL()
      .then((url) => {
        // Descargar el archivo utilizando el enlace de descarga
        const link = document.createElement("a");
        link.href = url;
        link.download = "https://firebasestorage.googleapis.com/v0/b/trapape.appspot.com/o/machotes%2FCARGAS%20MASIVAS%20TRAPAPE.xlsx?alt=media&token=e387b4f0-2310-4394-8017-e7a290891aa5"; // Reemplaza "nombre-del-machote.xlsx" con el nombre del archivo machote
        link.click();
      })
      .catch((error) => {
        console.log("Error al obtener la URL del archivo:", error);
      });
  };

  return (
    <div>
      <h2>Descargar Machote</h2>
      <Button variant="contained" onClick={handleDescargarMachote}>
        Descargar
      </Button>
    </div>
  );
};

export default DescargarMachote;
