import React, { useState } from "react";
import { storage } from "../utils/firebase";
import axios from "axios";
import Button from "@mui/material/Button";

const SubirCargasMasivas = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [uploadedUrl, setUploadedUrl] = useState("");

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = () => {
    if (selectedFile) {
      const storageRef = storage.ref();
      const fileRef = storageRef.child(selectedFile.name);
      const uploadTask = fileRef.put(selectedFile);

      uploadTask.on(
        "state_changed",
        (snapshot) => {
          const progress = Math.round(
            (snapshot.bytesTransferred / snapshot.totalBytes) * 100
          );
          setUploadProgress(progress);
        },
        (error) => {
          console.error("Error al subir el archivo:", error);
        },
        () => {
          uploadTask.snapshot.ref.getDownloadURL().then((downloadURL) => {
            setUploadedUrl(downloadURL);
            console.log("URL del archivo subido:", downloadURL);

            const userConsig = "valor-user-consig"; // Reemplaza con el valor real de userConsig
            const apiUrl = "https://api-trapape.azurewebsites.net/api/DownloadExcel/";

            const requestData = {
              URLExcel: downloadURL,
              userConsig: userConsig
            };

            axios
              .post(apiUrl, requestData)
              .then((response) => {
                console.log("Respuesta de la API:", response.data);
                // Realizar acciones adicionales con la respuesta de la API si es necesario
              })
              .catch((error) => {
                console.error("Error al llamar a la API:", error);
              });
          });
        }
      );
    }
  };

  return (
    <div>
      <h2>Subir Cargas Masivas</h2>
      <input type="file" onChange={handleFileChange} />
      <Button variant="contained" onClick={handleUpload}>Subir Archivo</Button>
      {uploadProgress > 0 && <p>Progreso: {uploadProgress}%</p>}
      {uploadedUrl && <p>URL del archivo subido: {uploadedUrl}</p>}
    </div>
  );
};

export default SubirCargasMasivas;
