import React, { useState } from 'react';
import { getStorage, ref, uploadBytesResumable, getDownloadURL } from 'firebase/storage';
import { useDropzone } from 'react-dropzone';
import { v4 as uuidv4 } from 'uuid';
import Button from '@mui/material/Button';

const FileUploader = () => {
  const [file, setFile] = useState(null);
  const [downloadURL, setDownloadURL] = useState(null);
  const [apiResponse, setAPIResponse] = useState(null);

  const handleFileUpload = () => {
    if (file) {
      const storage = getStorage();
      const fileName = `${uuidv4()}.${file.name.split('.').pop()}`;
      const storageRef = ref(storage, `uploads/${fileName}`);
      const uploadTask = uploadBytesResumable(storageRef, file);

      uploadTask.on(
        'state_changed',
        (snapshot) => {
          // Puedes mostrar el progreso de carga aquí si deseas
        },
        (error) => {
          // Maneja los errores de carga aquí
          console.error(error);
        },
        () => {
          // La carga se ha completado exitosamente
          console.log('Archivo subido con éxito');

          // Obtener la URL de descarga del archivo
          getDownloadURL(uploadTask.snapshot.ref).then((url) => {
            setDownloadURL(url);

            // Realizar la solicitud POST a la API
            /*const requestOptions = {
              method: 'POST',
              headers: { 'Content-Type': 'application/json' },
              body: JSON.stringify({ URLExcel: url, userConsig: '4V1okNHGntWVAdGzIGo2MdCeukI2' })
            };

            fetch('https://api-trapape.azurewebsites.net/api/DownloadExcel/', requestOptions)
              .then(response => response.json())
              .then(data => setAPIResponse(data))
              .catch(error => console.error(error));*/
          });
        }
      );
    }
  };

  const handleDrop = (acceptedFiles) => {
    // Establecer el archivo seleccionado en el estado
    setFile(acceptedFiles[0]);
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({ onDrop: handleDrop });

  return (
    <div>
      <div {...getRootProps()}>
        <input {...getInputProps()} />
        {isDragActive ? (
          <p>Suelta el archivo aquí...</p>
        ) : (
          <p>Arrastra y suelta un archivo aquí, o haz clic para seleccionar un archivo</p>
        )}
      </div>
      {file && (
        <p>Archivo seleccionado: {file.name}</p>
      )}
      <Button variant="contained" onClick={handleFileUpload} disabled={!file}>
        Subir archivo
      </Button>
      {downloadURL && (
        <p>URL de descarga: <a href={downloadURL} target="_blank" rel="noopener noreferrer">{downloadURL}</a></p>
      )}
      {apiResponse && (
        <p>Respuesta de la API: {JSON.stringify(apiResponse)}</p>
      )}
    </div>
  );
};

export default FileUploader;
