import { Grid, Paper, Typography, Container, Button } from "@mui/material";

import {useEffect, useState} from "react";
import {useNavigate} from "react-router-dom";
import {endSession, getSession, isLoggedIn} from "../utils/session";
import {readDataField, readData } from '../utils/firebaseService';

// Llamada a la funci贸n readData
readDataField('projects/proj_meqjHnqVDFjzhizHdj6Fjq/data/Loads', "userConsig", "x5Hu5ZQi8sbg2abS7mbvcYsjUUx2")
  .then((data) => {
    console.log(data);
  })
  .catch((error) => {
    console.error(error);
  }); 

export default function User() {
  let navigate = useNavigate();

  const [email, setEmail] = useState("");

  useEffect(() => {
    if (!isLoggedIn()) {
      navigate("/login");
    }
  
    let session = getSession();
    setEmail(session.email);
  
    console.log("Your access token is: " + session.accessToken);
  }, [navigate]);
  
  const onLogout = () => {
    endSession();
    navigate("/login");
  }

  // Llamada a la funci贸n readData
readDataField('projects/proj_meqjHnqVDFjzhizHdj6Fjq/data/Loads', "userConsig", "x5Hu5ZQi8sbg2abS7mbvcYsjUUx2").then((data) => {
  console.log(data);
  const cargaStatuses = ["status1", "status2", "status3", "status4", "status5"];
  
  const renderCargasByStatus = (status) => {
    const cargas = data.filter((carga) => carga.status === status);
  
    return cargas.map((carga) => (
      <Grid item xs={12} sm={6} md={4} lg={3} key={carga.IdLoad}>
        <Paper>
          <Typography>{carga.IdLoad}</Typography>
          {/* Otros detalles de la carga */}
        </Paper>
      </Grid>
    ));
  };

})
.catch((error) => {
  console.error(error);
});
  
  

  

  const MyComponent = () => {
    return (
      <Grid container spacing={2}>
        {cargaStatuses.map((status) => (
          <Grid item xs={12} key={status}>
            <Typography variant="h6" gutterBottom>
              {status}
            </Typography>
            <Grid container spacing={2}>
              {renderCargasByStatus(status)}
            </Grid>
          </Grid>
        ))}
      </Grid>
    );
  };

  return (
    <>
    <Container maxWidth="xs" sx={{ mt: 2 }}>
      <Typography variant="h6" component="h1" textAlign="center" gutterBottom>
        CARGAS
      </Typography>
    </Container><Container maxWidth="xs" sx={{ mt: 2 }}>
        <Typography variant="h6" component="h1" textAlign="center" gutterBottom>
          You're logged in as:
        </Typography>
        <Typography variant="h5" component="h1" textAlign="center" gutterBottom>
          {email}
        </Typography>
        <Typography variant="p" component="p" textAlign="center" gutterBottom>
          Check the console for your (access/session) token.
        </Typography>
        <Button variant="contained" color="error" onClick={onLogout} sx={{ mt: 3 }} fullWidth>
          Log out
        </Button>
      </Container>
      </>
  )
  }
